    def prevent_double_selection(self, item):
        """Đảm bảo chỉ có một checkbox được chọn giữa 'Có' và 'Không' trong cùng một hàng."""
        if item.column() in [1, 2]:  # Chỉ xử lý khi checkbox được chọn
            row = item.row()
            self.symptom_table.blockSignals(True)  # Ngăn vòng lặp vô hạn
            
            if item.column() == 1 and item.checkState() == Qt.Checked:  # Nếu chọn "Có"
                self.symptom_table.item(row, 2).setCheckState(Qt.Unchecked)
            elif item.column() == 2 and item.checkState() == Qt.Checked:  # Nếu chọn "Không"
                self.symptom_table.item(row, 1).setCheckState(Qt.Unchecked)

            self.symptom_table.blockSignals(False)  # Mở lại tín hiệu
